label("$H$",B,N);
label("$y$",(1,1.2),S);
label("$y$",(0.66,0.44),E);
label("$G$",(0.66,0),S);
label("$y$",(0.2,0.6),N);
label("$x$",(0.45,-0.1),S);
draw((0,0)--(1.2,1.6),linewidth(0.7));
label(scale(0.7)*rotate(55)*"$2-y$",(0.33,0.44),E);
label("1",(-0.8,0.2),N);
label("1",(-0.7,0),S);
draw((-1,0)--(0.66,0.88)--(0.66,0)--cycle,linewidth(0.7));

