A[0] = (5,0);
P[1] = (5,1);
A[1] = (9,1);
P[2] = (9,3);

P[3] = (12,6);
A[3] = (15,6);
P[4] = (15,10);

draw(P[0]--A[0]--P[1]--cycle);
draw(P[1]--A[1]--P[2]--cycle);
draw(P[3]--A[3]--P[4]--cycle);
draw(P[0]--P[4],dashed);

